note_models.css:
#deck{
margin-bottom:2em;
margin-top:0em;
color: #6c71c4;
font-size: 13pt;
}

.card {
    font-family: Menlo, baskerville, sans;
    font-size: 12.5pt;
    text-align: left;
    color: #D7DEE9;
    line-height: 1.6em;
    background-color: #333B45;
    max-width:800px;
    margin: auto;
    padding-bottom: 3em;
    padding-top: 2em;
    padding-left: 1em;
    padding-right: 1em;
}

img {
max-width: 750px;
}

img.small {
max-width: 400px;
display: block;
margin: auto;
}

.recap img {
max-width:100%;
}

img.masked-img {
filter: blur(8px)  opacity(50%);
}

.cloze {
    /*font-weight: bold;*/
    color: MediumSeaGreen; }

/* Bold underline, italic and link styles. */
b { 
    color: #C695C6 !important;
} 
u { 
    text-decoration: none; 
    color: #5EB3B3;
}
a { 
    color: LightGray; 
    font-style: normal;
}


/* For mobile. */
.mobile .card { 
    color: #D7DEE9; 
    background-color: #333B45; 
}

.mobile .tags:hover { 
    opacity: 1; 
    position: relative;
}

h1, h2, h3, h4, h5 {
    text-align:center;
    margin-bottom: 1em;
    margin-top: 2.5em;
}

h1 {
    font-size: 18pt;
    font-weight: normal;
    font-family: san-serif;
    margin-top: 0; /* To cancel the above rule. */
}

h2 {
    font-size: 16pt;
    font-weight: normal;
}

h3 {
    font-size: 15pt;
    font-weight: normal;
}

h4 {
   font-size: 13.5pt;
   font-weight: bold;
   margin-bottom: 0.5em;
}

p span.inline-heading {
  text-decoration: underline;
}

div#example {
margin-top: 4em;
}

div#back_only {
margin-top:2em;
}

/* For text that reveals to much on the front side, 
but isn't really meant to be guessed as a cloze. */
#front .back-only {
filter: blur(3px) opacity(25%);
/* Alternatively: */
/* visibility: hidden; */
}

hr#source {
	margin-top: 6em;
}

.update_note {
color: #8DB2D7;
font-style:oblique;
}

.em2 {
color: #75EFEF;
}

em.def {
color: #ffff77;
}

.def .cloze {
color: #ffff77;
  text-decoration: underline;
  text-decoration-color: MediumSeaGreen;
} 

div.theorem, div.definition, div.lemma, div.proposition {
   background: #4C3D45;
   //background: #444C56;
   padding: 0.5em 1em 0.5em 1em;
   max-width: 620px;
    color: #E7EEF9;
  /* margin: auto; */
   margin: 2em 3em 2em 3em;
}

.nightMode div.theorem, .nightMode div.definition, .nightMode div.lemma, .nightMode div.proposition {
  //background: #5C3837; //progressively lighter-> #5E3636;//#6B3E3D;//#704140;
 background: #523333;
} 

div.minor_lemma, div.minor_definition, div.minor_theorem {
  font-size: 95%;
  border:1px solid #999;
  padding: 0em 2em 1.0em 2em;
  margin: 2.5em 3em 2em 3em;
}    

/** em_recap seems like a bad name. Switching to just recap. **/
div.em_recap, div.recap, div.remark {
  font-size: 85%;
  border:1px solid #999;
  padding: 0em 2em 1.0em 2em;
  margin: 2.5em 3em 2em 3em;
}    

div.remark {
   padding-top: 0.5em; /* was previously 1.0em. Not sure why. */
   color: #CCCC;
}

div.recap h4, div.remark h4 {
margin-top: 0.8em;
}

.boxed {
border: 1px solid #999;
padding: 0em 2em 1.0em 2em;
margin: 2em 3em;
}

.larger_eq {
font-size: 110%;
}

.em_recap h4, .recap h4, .minor_lemma h4, .minor_definition h4 {
	margin-top: 1em;
}
.proposition h3, .lemma h3, .lemma h4, .definition h3, .definition h4, .theorem h3, .theorem h4 {
   margin-top: 0.5em;
}

/* Remarks within a paragraph. */
p.remark, span.remark {
  color: #CCCC;
  font-size: 85%;
}

/* Update: 'block_eq' isn't needed anymore. Custom JS handles it instead */
/* This is sometimes used when we want a block latex AND to use cloze hints.
Using both causes the front side to wrap the hint version in square brackets *before* the
newlines are added for the block quality of \[ \] latex blocks.*/
div.block_eq {
    display:block;
    text-align:center;
}



ol.lower_alpha > li {
  list-style-type: lower-alpha;
}

dl.horizontal {
  display: grid;
  grid-template-columns: fit-content fit-content;
  grid-row-gap: 0.8em; 
}


dl.horizontal dt {
  grid-column-start: 1;
}

dl.horizontal dd {
  grid-column-start: 2;
}
<!-- yellow is a nice color to use, btw -->
